import { useEffect, useState } from "react";
import { Table, TableHead, TableRow, TableBody, TableCell, styled, Button } from "@mui/material";
import { getUserdetails, deleteUser } from "../service/api";
import { Link } from "react-router-dom";

const StyledTable = styled(Table)`
width: 90%;
margin: 50px auto 0 auto;
`;

const Thead = styled(TableRow)`
background: #000;
& > th {
    color: #fff;
    font-size: 20px;
}
`

const TBody = styled(TableRow)`

& > th {
    color: #fff;
    font-size: 20px;
}
`

const UserList = () =>{

    const [users, setUSers] = useState([ ]);
 
    useEffect( () =>
    {
        getUserDetails();

    }, []
    ) 
    
    const getUserDetails = async() =>{
      let response = await getUsers();
      console.log(response);
      setUSers(response.data); 
    }
    
const deleteUserData = async (id) =>{
    await deleteUser(id);
    getUsersDetails();
} 

    return(
        <>
        <StyledTable>
            <TableHead>
                <Thead>
                    <TableCell>Id</TableCell>
                    <TableCell>Name</TableCell>
                    <TableCell>Username</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>Phone</TableCell>
                    <TableCell>Action</TableCell>
                </Thead>

            </TableHead>
            <TableBody>
                {
                    users.map(user =>(
                        <TBody>
                        <TableCell>{user.id}</TableCell>
                        <TableCell>{user.name}</TableCell>
                        <TableCell>{user.username}</TableCell>
                        <TableCell>{user.email}</TableCell>
                        <TableCell>{user.phone}</TableCell>
                        <TableCell>
                            
                        <Button variant="contained"  component={Link} to={`/edit/${user.id}`}>Edit</Button>
                            <Button variant="contained" color="secondary" onClick={() => deleteUserData(user.id)} style={{marginRight:10}}>Delete</Button>
                           
                        </TableCell>
                        </TBody>
                    ))
                }
                
            </TableBody>

        </StyledTable>
        </>

        

      

    );
}

export default UserList;
